import {AppDispatch} from "./store";
import {ICategory} from "../models/DataBaseItems";
import {categorySlice} from "./slices/CategorySlice";
import {addCategory} from "../http/itemsAPI";

export const createCategory = (category: string) => async(dispatch: AppDispatch) => {
    try {
        dispatch(categorySlice.actions.categoryCreation())
        await addCategory(category)
        dispatch(categorySlice.actions.categoryCreationSuccess())
    } catch (e) {
        dispatch(categorySlice.actions.categoryCreationError())
    }
}
