import React, {useEffect, useState} from 'react';
import {useAppDispatch, useAppSelector} from "../hooks/redux";
import classes from '../styles/Bars/SubcategoryBar.module.scss'
import {fetchCategories, setCurrentSubcategory} from "../store/ActionCreators/CategoryActionCreators";
import UIButton from "../UIKIT/UIButton";
import UIInput from "../UIKIT/UIInput";
import {FaCheck, FaPlus, FaTimes} from "react-icons/fa";

const SubcategoryBar = () => {
    const {subcategories, loading, error, currentSubcategory} = useAppSelector(state => state.categoryReducer)
    const dispatch = useAppDispatch()

    const [subcategoryCreation,setSubcategoryCreation] = useState<boolean>(false)

    return (
        <div className={classes['SubcategoryBar']}>
            <div  className={classes['SubcategoryBar__items']}>
                {subcategories && subcategories.map(i => (
                    <UIButton
                        type={i === currentSubcategory ? 'link-active' : 'link'}
                        onClick={() => dispatch(setCurrentSubcategory(i))}
                        key={i.id}
                    >{i.name}</UIButton>
                ))
                }
                {subcategoryCreation ?
                    <>
                        <UIInput
                            value={categoryCreationName}
                            type={'primary'}
                            placeholder={'Категория...'}
                            onChange={(e: React.FormEvent<HTMLInputElement>) => setCategoryCreationName(e.currentTarget.value)}
                        />
                        <>
                            <UIButton type={'icon'} onClick={e => categoryCreationHandler(e)}><FaCheck/></UIButton>
                            <UIButton type={'icon'} onClick={() => setCategoryCreation(false)}><FaTimes/></UIButton>
                        </>
                    </>
                    :
                    <UIButton type={'icon'} onClick={() => setCategoryCreation(true)}><FaPlus/></UIButton>
                }
                {loading && <p>loading....</p>}
                {error && <p>error....</p>}
            </div>


        </div>
    );
};

export default SubcategoryBar;
