import {AppDispatch} from "../store";
import {ICategory} from "../../models/DataBaseItems";
import {subcategorySlice} from "../slices/SubcategorySlice";
import {$host} from "../../http";

export const fetchSubcategories = (category: ICategory) => async(dispatch: AppDispatch) => {
    try {
        dispatch(subcategorySlice.actions.subcategoryFetching())
        const {data} = await $host.get('api/subcategory', {params: category})
        dispatch(subcategorySlice.actions.subcategoryFetchingSuccess(data))
    } catch (e) {
        dispatch(subcategorySlice.actions.categoryFetchingError())
    }
}

export const createSubcategory = (name: string) => async(dispatch: AppDispatch) => {
    try {
        dispatch(subcategorySlice.actions.categoryCreation())
        await $host.post('api/category', {name}) //authost !
        dispatch(subcategorySlice.actions.categoryCreationSuccess())
    } catch (e) {
        dispatch(subcategorySlice.actions.categoryCreationError())
    }
}

export const setCurrentSubcategory = (category: ICategory) => (dispatch: AppDispatch) => {
    dispatch(subcategorySlice.actions.subcategorySet(category))
}
