import {createSlice, PayloadAction} from "@reduxjs/toolkit";
import {ICategory} from "../../models/DataBaseItems";

interface CategoryState {
    categories: ICategory[];
    currentCategory: ICategory;
    loading: boolean;
    error: boolean
}

const initialState: CategoryState = {
    categories: [],
    currentCategory: {
        id: 0,
        name: '',
        createdAt: '',
        updatedAt: '',
    },
    loading: false,
    error: false
}

export const categorySlice = createSlice({
        name: 'category',
        initialState,
        reducers: {
            categorySet(state, action: PayloadAction<ICategory>){
                state.currentCategory = action.payload
            },
            categoryCreation(state){
                state.loading = true
                state.error = false
            },
            categoryCreationSuccess(state){
                state.loading = false
                state.error = false
            },
            categoryCreationError(state){
                state.loading = false
                state.error = true
            },
            categoryFetching(state){
                state.loading = true
                state.error = false
            },
            categoryFetchingError(state){
                state.loading = false
                state.error = true
            },
            categoryFetchingSuccess(state, action: PayloadAction<ICategory[]>){
                state.loading = false
                state.error = false
                state.categories = action.payload
                state.currentCategory = action.payload[0]
            }
        }
    }
)

export default categorySlice.reducer
