import React, {useEffect, useState} from 'react';
import {fetchCategories} from "../../http/itemsAPI";
import UIButton from "../../UIKIT/UIButton";
import {FaCheck, FaTrash} from "react-icons/fa";
import classes from '../../styles/Admin/AdminTopBar.module.scss'
import {ICategory} from "../../models/DataBaseItems";

const AdminTopBar = () => {
    const [categories,setCategories] = useState<ICategory[]>([])
    const [currentCategory, setCurrentCategory] = useState<ICategory>()

    useEffect(() => {
        fetchCategories()
            .then(data => setCategories(data))
        setCurrentCategory(categories[0])
    }, [])

    useEffect(() => {
        setCurrentCategory(categories[0])
    }, [currentCategory])
    return (
        <div className={classes['AdminTopBar']}>
            <div className={classes['AdminTopBar__categories']}>
                { categories && categories.map(i => (
                    <UIButton
                        type={i === currentCategory ? 'primary-small-active' : 'primary-small'}
                        onClick={() => setCurrentCategory(i)}
                        key={i.id}
                    >{i.name}</UIButton>
                ))}
            </div>
            <UIButton type={'primary'} onClick={() => console.log('sd')}>Отправить</UIButton>
            <UIButton type={'primary-small'} onClick={() => console.log('sd')}>Отправить</UIButton>
            <UIButton type={'primary-small-active'} onClick={() => console.log('sd')}>Отправить</UIButton>
            <UIButton type={'link'} onClick={() => console.log('sd')}>Отправить</UIButton>
            <UIButton type={'link-active'} onClick={() => console.log('sd')}>Отправить</UIButton>
            <UIButton type={'icon'} onClick={() => console.log('sd')}><FaCheck/></UIButton>
            <UIButton type={'icon'} onClick={() => console.log('sd')}><FaTrash/></UIButton>
        </div>
    );
};

export default AdminTopBar;
