import React, {useEffect, useState} from 'react';
import {fetchCategories, fetchSubcategories} from "../http/itemsAPI";
import UIButton from "../UIKIT/UIButton";
import {FaCheck, FaPlus, FaTimes, FaTrash} from "react-icons/fa";
import classes from '../styles/Admin/CategoryBar.module.scss'
import {ICategory, ISubcategory} from "../models/DataBaseItems";
import UIInput from "../UIKIT/UIInput";
import {createCategory} from "../store/CategoryActionCreators";

const CategoryBar = () => {
    const {loading} = useAppSelector
    const [categories,setCategories] = useState<ICategory[]>([])
    const [currentCategory, setCurrentCategory] = useState<ICategory>()

    const [subcategories,setSubcategories] = useState<ISubcategory[]>([])
    const [currentSubcategory, setCurrentSubcategory] = useState<ISubcategory>()

    const [categoryCreation, setCategoryCreation] = useState<boolean>(false)

    const [categoryCreationName, setCategoryCreationName] = useState<string>('')

    useEffect(() => {
        fetchCategories().then(data => setCategories(data))
    }, [])

    const subcategoryHandler = (i: ICategory) => {
        setCurrentCategory(i)
        fetchSubcategories(i).then(data => setSubcategories(data))
    }

    const categoryCreationHandler = () => {
        createCategory(categoryCreationName)
    }

    return (
        <div className={classes['CategoryBar']}>
            <div className={classes['CategoryBar__items']}>
                { categories && categories.map(i => (
                    <UIButton
                        type={i === currentCategory ? 'primary-small-active' : 'primary-small'}
                        onClick={() => subcategoryHandler(i)}
                        key={i.id}
                    >{i.name}</UIButton>
                ))}
                { categoryCreation ?
                    <>
                        <UIInput
                            value={categoryCreationName}
                            type={'primary'}
                            placeholder={'Категория...'}
                            onChange={(e: React.FormEvent<HTMLInputElement>) => setCategoryCreationName(e.currentTarget.value)}
                        />
                        <UIButton type={'icon'} onClick={() => categoryCreationHandler()}><FaCheck/></UIButton>
                        <UIButton type={'icon'} onClick={() => setCategoryCreation(false)}><FaTimes/></UIButton>
                    </>
                    :
                    <UIButton type={'icon'} onClick={() => setCategoryCreation(true)}><FaPlus/></UIButton>
                }
            </div>

            <div className={classes['AdminTopBar__subcategories']}>
                { subcategories && subcategories.map(i => (
                    <UIButton
                        type={i === currentSubcategory ? 'link-active' : 'link'}
                        onClick={() => setCurrentSubcategory(i)}
                        key={i.id}
                    >{i.name}</UIButton>
                ))}
            </div>
            {loading && <p>loading....</p>}
        </div>
    );
};

export default CategoryBar;
