import React, {useEffect, useState} from 'react';
import Admin from "./pages/Admin/Admin";
import {
    fetchAvailability,
    fetchBrand,
    fetchCategories, fetchColor, fetchMaterial,
    fetchModel,
    fetchSize,
    fetchSubcategories
} from "./http/itemsAPI";

const App = () => {
    const [categories, setCategories] = useState<object[]>()
    const [subcategories, setSubCategories] = useState<object[]>()
    const [brand,setBrand] = useState<object[]>()
    const [model,setModel] = useState<object[]>()
    const [size,setSize] = useState<object[]>()
    const [color,setColor] = useState<object[]>()
    const [availability,setAvailability] = useState<object[]>()
    const [material, setMaterial] = useState<object[]>()

    useEffect(() => {
        fetchCategories().then(data => setCategories(data))
        fetchSubcategories().then(data => setSubCategories(data))
        fetchBrand().then(data => setBrand(data))
        fetchModel().then(data => setModel(data))
        fetchMaterial().then(data => setMaterial(data))
        fetchColor().then(data => setColor(data))
        fetchAvailability().then(data => setAvailability(data))
        fetchSize().then(data => setSize(data))
    }, [])

    return (
        <div>
            <Admin/>
            <div>
                <b>Категории:</b>
                {categories?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Подкатегории:</b>
                {subcategories?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Бренд:</b>
                {brand?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Модель:</b>
                {model?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Цвет:</b>
                {color?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Материал:</b>
                {material?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Наличие:</b>
                {availability?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
            <div>
                <b>Размер:</b>
                {size?.map((i: any) => (
                    <p key={i.id}>{i.name}</p>
                ))}
            </div>
        </div>
    );
};

export default App;
