import React, {useEffect} from 'react';
import {useAppDispatch, useAppSelector} from "../hooks/redux";
import classes from '../styles/Bars/SubcategoryBar.module.scss'
import {fetchCategories, setCurrentSubcategory} from "../store/ActionCreators/CategoryActionCreators";
import UIButton from "../UIKIT/UIButton";

const SubcategoryBar = () => {
    const {subcategories, loading, error, currentSubcategory} = useAppSelector(state => state.categoryReducer)
    const dispatch = useAppDispatch()


    return (
        <div className={classes['SubcategoryBar']}>
            {subcategories && subcategories.map(i => (
                <UIButton
                    type={i === currentSubcategory ? 'link-active' : 'link'}
                    onClick={() => dispatch(setCurrentSubcategory(i))}
                    key={i.id}
                >{i.name}</UIButton>
            ))
            }
        </div>
    );
};

export default SubcategoryBar;
