import {createSlice, PayloadAction} from "@reduxjs/toolkit";
import {ISubcategory} from "../../models/DataBaseItems";

interface SubcategoryState {
    subcategories: ISubcategory[];
    currentSubcategory: ISubcategory;
    loading: boolean;
    error: boolean
}

const initialState: SubcategoryState = {
    subcategories: [],
    currentSubcategory: {
        id: 0,
        name: '',
        category_id: 0,
        createdAt: '',
        updatedAt: ''
    },
    loading: false,
    error: false
}

export const subcategorySlice = createSlice({
        name: 'category',
        initialState,
        reducers: {
            subcategorySet(state, action: PayloadAction<ISubcategory>){
                state.currentSubcategory = action.payload
            },
            subcategoryCreation(state){
                state.loading = true
                state.error = false
            },
            subcategoryCreationSuccess(state){
                state.loading = false
                state.error = false
            },
            subcategoryCreationError(state){
                state.loading = false
                state.error = true
            },
            subcategoryFetching(state){
                state.loading = true
                state.error = false
            },
            subcategoryFetchingError(state){
                state.loading = false
                state.error = true
            },
            subcategoryFetchingSuccess(state, action: PayloadAction<ISubcategory[]>){
                state.loading = false
                state.error = false
                state.subcategories = action.payload
                state.currentSubcategory = action.payload[0]
            }
        }
    }
)

export default subcategorySlice.reducer
